int umpi_mpi_generic_unpack (umpi_op_t*op,char*buf,int sender)
{
umpi_mpi_lookup_t *lookup;
unsigned index;
int temp, packed_size, oversized;

assert(op);
assert(buf);

bcopy (buf, &temp, sizeof(int));
assert(temp == UMPI_START_PACKED_OP);
index = sizeof(int);

bcopy (&buf[index], &packed_size, sizeof(int));
index += sizeof(int);

oversized = (packed_size > UMPI_MAX_MSG_BYTES);
if (oversized) {
bcopy (&buf[index], &temp, sizeof(int));
assert(temp == UMPI_END_SPLIT_PACKED_OP);
buf = (char *) malloc (sizeof(char) * packed_size);
assert(buf);
//umpi_mgr_large_op_recv(buf, packed_size, sender);
bcopy (buf, &temp, sizeof(int));
assert(temp == UMPI_START_SPLIT_PACKED_OP);
index = sizeof(int);

bcopy (&buf[index], &temp, sizeof(int));
index += sizeof(int);
assert(temp == packed_size);
}

bcopy (&buf[packed_size - sizeof(int)], &temp, sizeof(int));
assert(temp == UMPI_END_PACKED_OP);

bcopy (&buf[index], &(op->op), sizeof(op->op));
index += sizeof(op->op);

bcopy (&buf[index], &(op->order), sizeof(op->order));
index += sizeof(op->order);

bcopy (&buf[index], &(op->rank), sizeof(op->rank));
index += sizeof(op->rank);

bcopy (&buf[index], &(op->pc), sizeof(op->pc));
index += sizeof(op->pc);

bcopy (&buf[index], &(op->seq_num), sizeof(op->seq_num));
index += sizeof(op->seq_num);

lookup = get_struct_by_opcode (op->op);
index += lookup->unpack_op (op, &buf[index]);

if (oversized) free (buf);

assert (index == (packed_size - sizeof(int)));

return packed_size;
} /* umpi_unpack_generic_op */




